const ajaxGetUser = async (jwt) => {
    const url = 'http://localhost:3100/api/v1/who-am-i';
    const res = await fetch(
        url, {
        method: "GET",
        mode: "cors",
        cache: "no-cache",
        credentials: "same-origin",
        headers: {
            'Content-type': 'application/json',
            'Access-Control-Allow-Origin': '*',
            'x-access-token': jwt
        }
    }
    );
    return res;
}

const destroyToken = (token) => {
    if(token !== ''){
        localStorage.removeItem('tpken');
    }
}

function Protected({ children }) {

    const [navigate, setNav] = useState('');

    useEffect(() => {
        try {
            let token = localStorage.getItem('token');
            token = token == null ? '' : token;
            console.log(token);
            ajaxGetUser(token).then(result => {
                result.json().then(json => {
                    if (result.status !== 200) {
                        destroyToken(token);
                        setNav('/login')
                    }
                    this.setState({ loading: false });
                    this.setState({ user: json.users });
                }).catch(err => {
                    console.log(err);
                    destroyToken(token);
                    setNav('/login')
                })
            }).catch(err => {
                console.log(err);
                destroyToken(token);
                setNav('/login')
            })
        } catch (error) {
            console.log(error);
            setNav('/login')
        }
    })

    return (
        navigate === '' ? children : <Navigate replace={navigate} />
    )

}

export default Protected;
