/* eslint-disable no-unreachable */
import React, { useEffect, useState } from 'react';
import { Dashboard, Contact, Blog } from '../../Components';
import { Navigate, Route } from 'react-router-dom';
import '../../index.css';

const ajaxGetUser = async (jwt) => {
    const url = 'http://localhost:3100/api/v1/who-am-i';
    const res = await fetch(
        url, {
        method: "GET",
        mode: "cors",
        cache: "no-cache",
        credentials: "same-origin",
        headers: {
            'Content-type': 'application/json',
            'Access-Control-Allow-Origin': '*',
            'x-access-token': jwt
        }
    }
    );
    return res;
}

const destroyToken = (token) => {
    if (token !== '') {
        localStorage.removeItem('token');
    }
}

function ProtectedRoute() {

    const [navigate, setNav] = useState('');
    const [user, setUser] = useState(undefined);

    useEffect(() => {
        try {
            let token = localStorage.getItem('token');
            token = token == null ? '' : token;
            console.log(token);
            ajaxGetUser(token).then(result => {
                result.json().then(json => {
                    if (result.status !== 200) {
                        destroyToken(token);
                        setNav('/login')
                    }
                    setUser(json.user);
                }).catch(err => {
                    console.log(err);
                    destroyToken(token);
                    setNav('/login')
                })
            }).catch(err => {
                console.log(err);
                destroyToken(token);
                setNav('/login')
            })
        } catch (error) {
            console.log(error);
            setNav('/login')
        }
    })

    return (
        navigate === '' ? (
            <Route>
                <Route
                    path=""
                    element={
                        <Dashboard user={user} />
                    } />
                <Route
                    path="blog"
                    element={
                        <Blog user={user} />
                    } />
                <Route
                    path="contact"
                    element={
                        <Contact user={user} />
                    } />
            </Route>
        ) : (
            <Route element={<Navigate to={navigate} />}></Route>
        )
    )

}

export { ProtectedRoute };
