/* eslint-disable no-unreachable */
import React from 'react';
import { Navigate } from 'react-router-dom';
import '../../index.css';

class Protected extends React.Component{
     state = {
        user: undefined,
        loading: true
     }

    constructor() {
        super()
        try {
            let token = localStorage.getItem('token');
            token = token == null ? '' : token;
            console.log(token);
            this.#ajaxGetUser(token).then(result => {
                result.json().then(json => {

                    if (result.status !== 200) {
                        console.log(result.status);
                        return (
                            <Navigate to='/login' />
                        )
                    }
                    this.setState({loading: false});
                    this.setState({user: json.users});
                }).catch(err => {
                    console.log(err);
                    return (
                        <Navigate to='/login' />
                    )
                })
            }).catch(err => {
                console.log(err);
                return (
                    <Navigate to='/login' />
                )
            })
        } catch (error) {
            console.log(error);
            return (
                <Navigate to='/login' />
            )
        }
    }

    #ajaxGetUser = async (jwt) => {
        const url = 'http://localhost:3100/api/v1/who-am-i';
        const res = await fetch(
            url, {
            method: "GET",
            mode: "cors",
            cache: "no-cache",
            credentials: "same-origin",
            headers: {
                'Content-type': 'application/json',
                'Access-Control-Allow-Origin': '*',
                'x-access-token': jwt
            }
        }
        );
        return res;
    }

    render({ children }) {
        return (
            { children }
        );
    }

}

export default new Protected();
