import React, { useState, useEffect, useRef } from 'react';
import { Navigate } from 'react-router-dom';
import '../../index.css';
import Navbar from '../Layouts/standart';

function Blog() {

    const [imgFile, setImg] = useState('%PUBLIC_URL%/logo192.png');
    const fileRef = useRef();

    const handleChange = () => {
        const img = fileRef.current.files[0];
        const reader = new FileReader();

        reader.addEventListener('load', ()=>{
            setImg(reader.result);
        });

        reader.readAsDataURL(img);
    }

    useEffect(() => {
        document.getElementById('exInFile').addEventListener('change', ()=>{
            handleChange();
        })
    })

    return (
        <div>
            <Navbar></Navbar>
            <div>
                <h1>Ini halaman Blog</h1>
                <h3 className='mt-5'>Inputkan gambar</h3>
                <img src='/public/logo192.png'></img>
                <div className='container'>
                    <div className="mb-3">
                        <label htmlFor="exInFile" className="form-label">Gambar</label>
                        <input type="file" className="form-control" id="exInFile" aria-describedby="emailHelp" />
                    </div>
                </div>

            </div>
        </div>
    );
}

export default Blog
